perl -e '$n=0;$N=0;open IN1,"gzip -dc <Read1.fq.gz> |"; open IN2,"gzip -dc <Read2.fq.gz> |";  open OUT,"| gzip > /Path/To/RA.fq.gz"; open OUT2,"| gzip > /Path/To/I1.fq.gz";  while(<IN1>){$N++; $seq="\@ST-E0:0:SIMULATE:2:0:0:$N"; print OUT "$seq 1:N:0\n"; $S=<IN1>;print OUT "$S"; $S=<IN1>;print OUT "$S"; $S=<IN1>;$S=~s/!/#/g;print OUT "$S";  $S=<IN2>; print OUT"$seq 3:N:0\n"; $S=<IN2>;print OUT "$S"; $S=<IN2>;print OUT "$S"; $S=<IN2>;$S=~s/!/#/g;print OUT "$S"; print OUT2 "$seq 2:N:0\nTTCACGCG\n\+\nAAFFFKKK\n"; } close(IN1);close(IN2);close(OUT);close(OUT2);'
